import React from 'react';
import Axios from 'axios';
import { Redirect } from 'react-router';
import { connect } from 'react-redux';
import {
  Form,
  Button,
  Card,
  Container,
  Row,
  Col,
} from 'react-bootstrap';
// import { Image as CloudinaryImage } from 'cloudinary-react';
import NavBar from '../../NavBar';
import { URL } from '../../env';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";
import Moment from 'react-moment';
import moment from 'moment';

class homepage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      products: [],
      city: localStorage.getItem('city'),
      status: 'active',
      keyword: undefined,
      organizerName: undefined,
      value: new Date(),
      startTime: undefined,
      endTime: undefined,
      redirectVar: false,
      search: false,
      resultTable: [],
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleChangeD = this.handleChangeD.bind(this);
    this.handleChangeK = this.handleChangeK.bind(this);
    this.handleChangeO = this.handleChangeO.bind(this);
    this.startTime = this.startTime.bind(this);
    this.endTime = this.endTime.bind(this);
  }


  componentDidMount() {

    this.getData();
  }

  getData() {
    const restList = [];

    console.log(this.state.city);
    Axios.get(`${URL}/event/filter`, {
      params: {
        city: this.state.city,
        status: this.state.status,
        keyword: this.state.keyword,
        organizerName: this.state.organizerName,
        startTime: this.state.startTime,
        endTime: this.state.endTime,
      }
    })
      .then((res) => {
        if (res) {
          console.log(res);
          console.log(res.data);
          if (res.data.length >= 0) {
            // eslint-disable-next-line no-plusplus
            for (let i = 0; i < res.data.length; i++) {
              restList.push(res.data[i]);
            }
          }
          this.setState({ products: restList });
        }
      }).catch((err) => {
        throw err;
      });
  }

  finalSearch = (userData) => {
    console.log(userData);
    Axios.post('http://localhost:3001/searchItem', userData)
      .then((res) => {
        if (res.status === 200) {
          console.log(res.data);
          this.setState({ resultTable: res.data });
          this.setState({ search: true });
        } else {
          // this.setState({ search: false });
        }
      });
  }

  handleChange = (e) => {
    if(e.target.value === ''){
      this.setState({ city: undefined });
    }
    else{
    this.setState({ city: e.target.value });
    console.log(this.state.city);
    }
  }

  handleChangeD = (e) => {
    this.setState({ status: e.target.value });
    console.log(e.target.value);
  }


  handleChangeK = (e) => {
    if(e.target.value === ''){
      this.setState({ keyword: undefined });
    }
    else{
    this.setState({ keyword: e.target.value });
    console.log(this.state.keyword);
    }
  }


  handleChangeO = (e) => {
    if(e.target.value === ''){
      this.setState({ organizer: undefined });
    }
    else{
    this.setState({ organizer: e.target.value });
    console.log(this.state.organizer);
    }
  }

  startTime = (event) => {
    console.log(event);
  //   console.log(event.target.value);
  const startTime = moment(event).format('YYYY-MM-DDThh:mm:ss').toString();
  console.log(startTime);
  this.setState({
    startTime: startTime,
  });
  console.log(this.state.startTime);
}

  endTime = (event) => {
      console.log(event);
  //   console.log(event.target.value);
  const endTime = moment(event).format('YYYY-MM-DDThh:mm:ss').toString();
  console.log(endTime);
  this.setState({
      endTime: endTime,
  });
  console.log(this.state.endTime);
  }


  //  componentDidUpdate(props, state) {
  //   if (state.city !== this.state.city) {
  //     console.log('input changed');
  //     this.getData();
  //   }
  // }

  handleSearch = (e) => {
    e.preventDefault();
    if(localStorage.getItem('city') === this.state.city){
      this.setState({city: undefined})
    }
    this.getData();
  }

  handleVisit = (event) => {
    event.preventDefault();
    const visitdata = {
      email: event.target.value,
    };
    console.log(visitdata);
    this.setState({
      remail: visitdata,
      redirectVar: true,
    });
    console.log(this.state.remail);
    console.log(this.state.redirectVar);
    Axios.post('http://localhost:3001/sr', visitdata)
      .then((res) => {
        console.log(res.status);
      });
  }

  handleFavourite = (event) => {
    event.preventDefault();
    const visitdata = {
      email: event.target.id,
    };
    console.log(visitdata);
    this.setState({
      remail: visitdata,
    });
    console.log(this.state.remail);
    Axios.post('http://localhost:3001/markfavourite', visitdata)
      .then((res) => {
        console.log(res.status);
      });
  }


  render() {
    console.log(this.state.products);
    console.log(this.state.resultTable);
    let redirectVar = null;
    if (this.state.redirectVar) {
      redirectVar = <Redirect to="/seerestaurant" />;
    }
      return (
      <div>
        <NavBar />
        {redirectVar}
        <div>
            <Form inline>
                <Container>
                    <Row>
                        <Col>
                        <input type="text" name="inSearch" placeholder=" Search Locations " style={{ width: '200px', height: '35px' }} onChange={this.handleChange} required />
                        &nbsp; &nbsp; &nbsp;
                        <label>
                        Status:
                        <select onChange={this.handleChangeD}>
                          <option value="active">Select</option>
                          <option value="active">Active</option>
                          <option value="openforregistration">OpenForRegistration</option>
                          <option value="all">All</option>
                          </select>
                        </label>
                        &nbsp; &nbsp; &nbsp;
                        <input type="text" name="inSearch" placeholder=" Keyword " style={{ width: '200px', height: '35px' }} onChange={this.handleChangeK} required />
                        &nbsp; &nbsp; &nbsp;
                        <input type="text" name="inSearch" placeholder=" Organizer " style={{ width: '200px', height: '35px' }} onChange={this.handleChangeO} required />
                        &nbsp; &nbsp; &nbsp;
                        &nbsp; &nbsp; &nbsp;
                        <div className="row">
              <div className="form-group">
              Start Time
                <DatePicker
              selected={ this.state.value }
              onChange={ this.startTime }
              showTimeSelect
              timeFormat="HH:mm"
              timeCaption="time"
              dateFormat="yyyy-MM-dd hh:mm aa"
                 />
                 <br />
              </div>
            </div>
            <div className="row">
              <div className="form-group">
              End Time
                <DatePicker
              onChange={ this.endTime }
              showTimeSelect
              timeFormat="HH:mm"
              timeCaption="time"
              dateFormat="yyyy-MM-dd hh:mm aa"
                 />
                 <br />
              </div>
            </div>
                        <Button type="submit" onClick={this.handleSearch}>Search</Button>
                        </Col>
                    </Row>
                    </Container>
                    </Form>
          <Form inline>
            <Container>
              <Row>
              {this.state.products.map((item) => <Col>
               <Card style={{ width: '20rem', margin: '2rem' }}>
                <Card.Header>
                  {item.title}
                </Card.Header>
                <Card.Body>
                  <Card.Title>Organizer: {item.organizer.screenName}</Card.Title>
                  <Card.Text>
                    {item.address.city}
                  </Card.Text>
                  <Card.Text>
                    {item.startDateTime}
                    &nbsp; &nbsp; &nbsp;
                  </Card.Text>
                  <Button variant="success" id={item.p_id} value={item.email} onClick={this.handleVisit}>Visit</Button>
                  <Button variant="outline-primary" id={item.email} value={item.email} onClick={this.handleFavourite}>Register</Button>
                </Card.Body>
              </Card>
              </Col>)}
              </Row>
            </Container>
          </Form>
        </div>
      </div>
      );

  }
}

export default homepage;
