      description: description,
    });
    console.log(this.state.description);
  }

  startDateTime = (event) => {
      console.log(event);
    //   console.log(event.target.value);
    const startDateTime = moment(event).format('YYYY-MM-DD HH:MM');
    console.log(startDateTime);
    this.setState({
      startDateTime: startDateTime,
    });
    console.log(this.state.startDateTime);
  }

    endDateTime = (event) => {
        console.log(event);
    //   console.log(event.target.value);
    const endDateTime = moment(event).format('YYYY-MM-DD HH:MM');
    console.log(endDateTime);
    this.setState({
        endDateTime: endDateTime,
    });
    console.log(this.state.endDateTime);
    }

    signUpDeadline = (event) => {
        console.log(event);
    //   console.log(event.target.value);
    const signUpDeadline = moment(event).format('YYYY-MM-DD HH:MM');
    console.log(signUpDeadline);
    this.setState({
        signUpDeadline: signUpDeadline,
    });
    console.log(this.state.signUpDeadline);
    }


  streetInputHandler = (event) => {
    console.log(event.target.value);
    const street = event.target.value;
    console.log(street);
    this.setState({
      street: street,
    });
    console.log(this.state.street);
  }

  numberInputHandler = (event) => {
    console.log(event.target.value);
    const number = event.target.value;
    console.log(number);
    this.setState({
      number: number,
    });
    console.log(this.state.number);
  }

  cityInputHandler = (event) => {
    console.log(event.target.value);
    const city = event.target.value;
    console.log(city);
    if(city != ''){
    this.setState({
      city: city,
    });
  }
    console.log(this.state.city);
  }

  stateInputHandler = (event) => {
    console.log(event.target.value);
    const state = event.target.value;
    console.log(state);
    if(state != ''){
    this.setState({
      state: state,
    });
  }
    console.log(this.state.state);
  }

  zipInputHandler = (event) => {
    console.log(event.target.value);
    const zip = event.target.value;
    console.log(zip);
    if(zip != ''){
    this.setState({
      zip: zip,
    });}
    console.log(this.state.zip);
  }

  handleSubmit = (event) => {
    event.preventDefault();
    const {
      name,
      username,
      type,
      gender,
      description,
      street,
      number,
      city,
      state,
      zip
    } = this.state;
    const baseURL = process.env.BACKEND_URL;
    const email = localStorage.getItem('email');
    const password = localStorage.getItem('password');
    const organization = type;
    const fullName = name;
    const screenName = username;
    console.log(email, password, fullName, screenName, organization, gender, description, street, number, city, state, zip);
    console.log(URL);
    const reg = {email, password, fullName, screenName, organization, gender, description, 
      address : {street, number, city, state, zip}};
      console.log(reg);
      const regf = JSON.stringify(reg);

      var config = {
        method: 'post',
        url: `${URL}/user/newUser`,
        headers: { 
          'Content-Type': 'application/json'
        },
        data : regf
      };

      Axios(config)
      .then( (response) => {
        console.log(JSON.stringify(response));
        console.log(response.status);
        if(response.status === 200){
          // console.log("DASHD");
          this.setState({
            redirect: true
          });
        }
      })
      .catch(function (error) {
        console.log(error);
      });

    // Axios.post(`${URL}/user/newUser`, {
    //   method: "POST",
    //   headers: {
    //     'Accept': 'application/json',
    //     'Content-Type': 'application/json',
    //   },
    //   body: JSON.stringify(datar)
    // }).then((response) => {
    //   console.log('Status Code : ', response.data.status);
    //   console.log(response);
    //   const status = response.data.status;
    //   if (status === 1062) {
    //     this.setState({
    //       // redirect: false,
    //       emailDup: true,
    //     });
    //   } else {
    //     this.setState({
    //       // redirect: true,
    //       emailDup: false,
    //     });
    //     this.props.dispatch({
    //       type: 'USER_REGISTERED',
    //       payload: true,
    //     });
    //   }

      // props.history.push('/login');
      // <Redirect to='/login'/>
    // }); 
  }

  render() {
    let redirectVar = null;
    if (this.state.redirect) {
      redirectVar = <Redirect to="/login" />;
    }
    const usernameError = this.state.usernameError;
    const nameError = this.state.nameError;
    return (
      <div>
        {redirectVar}
        <NavBar />
        <div className="container">
          <form onSubmit={this.handleSubmit}>
            <div className="row">
              <div className="col-xs" />
              <div className="col-xs">
                <HeadText>
                  Create Event
                  <br />
                </HeadText>
              </div>
            </div>
            <div className="row">
              <div className="col-xs" />
              <div className="col-xs">
                <OverallText>
                  <input type="text" name="title" placeholder=" Title " style={{ width: '390px', height: '35px' }} onChange={this.titleInputHandler} required />
                  <br />
                </OverallText>
              </div>
            </div>
            <div className="row">
              <div className="col-xs" />
              <div className="col-xs">
                <OverallText>
                  <input type="text" name="description" placeholder=" Description " style={{ width: '390px', height: '35px' }} onChange={this.descriptionInputHandler} required />
                  <br />
                </OverallText>
              </div>
            </div>
            <div className="row">
              <div className="form-group">
              <OverallText>Start Time</OverallText>
                <DatePicker
              selected={ this.state.value }
              onChange={ this.startDateTime }
              showTimeSelect
              timeFormat="HH:mm"
              timeCaption="time"
            //   dateFormat="yyyy-MM-dd hh:mm aa"
                 />
                 <br />
              </div>
            </div>
            <div className="row">
              <div className="form-group">
              <OverallText>End Time</OverallText>
                <DatePicker
              selected={ this.state.value }
              onChange={ this.endDateTime }
              showTimeSelect
              timeFormat="HH:mm"
              timeCaption="time"
            //   dateFormat="yyyy-MM-dd hh:mm aa"
                 />
                 <br />
              </div>
            </div>
            <div className="row">
              <div className="form-group">
              <OverallText>SignUp Deadline</OverallText>
                <DatePicker
              selected={ this.state.value }
              onChange={ this.signUpDeadline }
              showTimeSelect
              timeFormat="HH:mm"
              timeCaption="time"
            //   dateFormat="yyyy-MM-dd hh:mm aa"
                 />
                 <br />
              </div>
            </div>
            <br />
            <div className="row">
              <div className="col-xs" />
              <div className="col-xs">
                <OverallText>
                  <input type="text" name="street" placeholder=" Street " style={{ width: '390px', height: '35px' }} onChange={this.streetInputHandler} required />
                </OverallText>
              </div>
            </div>
            <div className="row">
              <div className="col-xs" />
              <div className="col-xs">
                <OverallText>
                  <input type="text" name="number" placeholder=" Number " style={{ width: '390px', height: '35px' }} onChange={this.numberInputHandler} required />
                </OverallText>
              </div>
            </div>
            <div className="row">
              <div className="col-xs" />
              <div className="col-xs">
                <OverallText>
                  <input type="text" name="city" placeholder=" City (San Jose) " style={{ width: '390px', height: '35px' }} onChange={this.cityInputHandler} required />
                </OverallText>
              </div>
            </div>
            <div className="row">
              <div className="col-xs" />
              <div className="col-xs">
                <OverallText>
                  <input type="text" name="state" placeholder=" State (California) " style={{ width: '390px', height: '35px' }} onChange={this.stateInputHandler} required />
                </OverallText>
              </div>
            </div>
            <div className="row">
              <div className="col-xs" />
              <div className="col-xs">
                <OverallText>
                  <input type="text" name="zip" placeholder=" Zip (95126) " style={{ width: '390px', height: '35px' }} onChange={this.zipInputHandler} required />
                </OverallText>
              </div>
            </div>

            <div className="row">
              <div className="col-xs" />
              <div className="col-xs">
                <OverallText>
                  <input type="button" value="Register" style={{ width: '390px', height: '35px', backgroundColor: '#7bb420' }} onClick={this.handleSubmit} />
                </OverallText>
              </div>
            </div>
          </form>
          <div className="row">
            <div className="col-xs" />
            <div className="col-xs">
              <OverallText>
                Already have an account?
                <Router forceRefresh>
                  <Link to="/login" onClick={() => history.push('/login')} style={{ color: 'green' }}> Login</Link>
                  <Switch>
                    <Route exact path="/login" component={UserLogin} />
                  </Switch>
                </Router>
              </OverallText>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default createEvent;
