/* eslint-disable  */
import React from 'react';
import axios from 'axios';
import { Redirect } from 'react-router';
import { connect } from 'react-redux';
import {
  Form,
  Button,
  Card,
  Container,
  Row,
  Col,
} from 'react-bootstrap';
// import { Image as CloudinaryImage } from 'cloudinary-react';
import NavBar from '../../NavBar';
import { URL } from '../../env';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";
import Moment from 'react-moment';
import moment from 'moment';

class time extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      time: undefined,
      tempTime: undefined,
      days: undefined,
      months: undefined,
      year: undefined,
    };
    this.handleChange = this.handleChange.bind(this);
  }


  componentDidMount() {

    axios.get(`${URL}/systemTime`)
      .then((res) => {
        if (res) {
          console.log(res);
          const dt = moment(res.data).format('MM-DD-YYYY hh:mm').toString();
          console.log(dt);
          this.setState({ time: dt });
        }
      }).catch((err) => {
        throw err;
      });
  }

  startTime = (event) => {
    console.log(event);
  const tempTime = moment(event.target.value).format('MM-DD-YYYY hh:mm').toString();
  console.log(tempTime);
  this.setState({
    tempTime: tempTime,
  });
  console.log(this.state.tempTime);
}


  handleChange = (e) => {
    console.log(event);
  const startTime = moment(event.target.value).format('YYYY-MM-DDThh:mm:ss').toString();
  console.log(startTime);
  this.setState({
    startTime: startTime,
  });
  console.log(this.state.startTime);
  }


  render() {
    console.log(this.state.products);
    console.log(this.state.resultTable);
    let redirectVar = null;
    if (this.state.redirectVar) {
      redirectVar = <Redirect to="/eventDetails" />;
    }
      return (
      <div>
        <NavBar />
        {redirectVar}
        <div>
          <Form inline>
            <Container>
              <Row>
              <Col>
               <Card style={{ width: '25rem', margin: '2rem' }}>
                <Card.Header>
                  Set Virtual Time
                </Card.Header>
                <Card.Body>
                  <Card.Title>Current Virtual Time: {moment(this.state.time).format('YYYY-MM-DD hh:mm').toString()}</Card.Title>
                  {/* <Card.Text>
                    {item.address.city}
                  </Card.Text>
                  <Card.Text>
                    {moment(item.startDateTime).format('YYYY-MM-DD hh:mm').toString()}
                    &nbsp; &nbsp; &nbsp;
                    {moment(item.signUpDeadline).format('YYYY-MM-DD hh:mm').toString()}
                  </Card.Text> */}
                  <Card.Text>
                  <input type='date' onChange={this.startTime}></input>
                  </Card.Text>
                  <Button variant="success"  onClick={this.handleChange}>Set Time</Button>
                </Card.Body>
              </Card>
              </Col>
              </Row>
            </Container>
          </Form>
        </div>
      </div>
      );

  }
}

export default time;
