/* eslint-disable */
/* eslint-disable react/prop-types */
/* eslint-disable arrow-body-style */
/* eslint-disable prefer-destructuring */
/* eslint-disable react/destructuring-assignment */
import React from 'react';
// import ReactDOM from 'react-dom';
import 'bootstrap/dist/css/bootstrap.css';
import Axios from 'axios';
import {
  BrowserRouter as Router,
  Link,
  Route,
  Switch,
} from 'react-router-dom';
import { Redirect } from 'react-router';
import { connect } from 'react-redux';
import styled from 'styled-components';
import history from './history';
// eslint-disable-next-line import/no-cycle
import UserLogin from './index';
import NavBar from '../../NavBar';

const HeadText = styled.h2`
    font-size: 30px;
    font-weight: 500;
    line-height: 1.24;
    color: ##000000;
    float: left;
    // z-index: 0;
    // margin: 0;
    //padding-left: 150px;
`;
const OverallText = styled.h2`
    font-size: 18px;
    font-weight: 300;
    line-height: 1.00;
    color: ##000000;
    float: left;
    // z-index: 0;
    // margin: 0;
    //padding-left: 150px;
`;

class userReg extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: '',
      username: '',
      contact: '',
      email: '',
      password: '',
      type: '',
      // register: false,
      // redirect: null,
      nameValid: '',
      usernameValid: '',
      contactValid: '',
      emailValid: '',
      passwordValid: '',
      emailDup: '',
      usernameError: '',
      contactError: '',
      emailError: '',
      passwordError: '',
      emailDupError: '',
    };
    this.nameInputHandler = this.nameInputHandler.bind(this);
    this.usernameInputHandler = this.usernameInputHandler.bind(this);
    this.contactInputHandler = this.contactInputHandler.bind(this);
    this.emailInputHandler = this.emailInputHandler.bind(this);
    this.passwordInputHandler = this.passwordInputHandler.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  // componentDidMount() {
  //   this.setState({
