import 'react-datepicker/dist/react-datepicker.css';
import '../../styles/Home.css';

import {
	BsFillCalendarWeekFill,
	BsPeopleFill,
	BsPersonFill,
} from 'react-icons/bs';
import { Button, Card, Col, Container, Form, Row } from 'react-bootstrap';

import Axios from 'axios';
import DatePicker from 'react-datepicker';
import { ImLocation } from 'react-icons/im';
import Moment from 'react-moment';
// import { Image as CloudinaryImage } from 'cloudinary-react';
import NavBar from '../../NavBar';
import React from 'react';
import { Redirect } from 'react-router';
import { URL } from '../../env';
import { connect } from 'react-redux';
import moment from 'moment';

class homepage extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			products: [],
			city: localStorage.getItem('city'),
			status: 'active',
			keyword: undefined,
			organizerName: undefined,
			startTime: undefined,
			endTime: undefined,
			event_id: null,
			redirectVar: false,
			search: false,
			resultTable: [],
		};
		this.handleChange = this.handleChange.bind(this);
		this.handleChangeD = this.handleChangeD.bind(this);
		this.handleChangeK = this.handleChangeK.bind(this);
		this.handleChangeO = this.handleChangeO.bind(this);
		this.handleSearch = this.handleSearch.bind(this);
		this.startTime = this.startTime.bind(this);
		this.endTime = this.endTime.bind(this);
		this.handleSet = this.handleSet.bind(this);
	}

	componentDidMount() {
		this.getData();
	}

	getData() {
		const restList = [];

		console.log(this.state.city);
		console.log(this.state.startTime);
		console.log(this.state.endTime);
		Axios.get(`${URL}/event/filter`, {
			params: {
				city: this.state.city,
				status: this.state.status,
				keyword: this.state.keyword,
				organizerName: this.state.organizerName,
				startTime: this.state.startTime,
				endTime: this.state.endTime,
			},
		})
			.then((res) => {
				if (res) {
					console.log(res);
					console.log(res.data);
					if (res.data.length >= 0) {
						// eslint-disable-next-line no-plusplus
						for (let i = 0; i < res.data.length; i++) {
							restList.push(res.data[i]);
						}
					}
					this.setState({ products: restList });
				}
			})
			.catch((err) => {
				throw err;
			});
	}

	handleChange = (e) => {
		if (e.target.value === '') {
			this.setState({ city: undefined });
		} else {
			this.setState({ city: e.target.value });
			console.log(this.state.city);
		}
	};

	handleChangeD = (e) => {
		this.setState({ status: e.target.value });
		console.log(e.target.value);
	};

	handleChangeK = (e) => {
		if (e.target.value === '') {
			this.setState({ keyword: undefined });
		} else {
			this.setState({ keyword: e.target.value });
			console.log(this.state.keyword);
		}
	};

	handleChangeO = (e) => {
		if (e.target.value === '') {
			this.setState({ organizerName: undefined });
		} else {
			this.setState({ organizerName: e.target.value });
			console.log(this.state.organizerName);
		}
	};

	startTime = (event) => {
		console.log(event);
		//   console.log(event.target.value);
		const startTime = moment(event.target.value)
			.format('YYYY-MM-DDThh:mm:ss')
			.toString();
		console.log(startTime);
		this.setState({
			startTime: startTime,
		});
		// localStorage.setItem('startTime', startTime);
		console.log(this.state.startTime);
	};

	endTime = (event) => {
		console.log(event);
		//   console.log(event.target.value);
		const endTime = moment(event.target.value)
			.format('YYYY-MM-DDThh:mm:ss')
			.toString();
		console.log(endTime);
		this.setState({
			endTime: endTime,
		});
		console.log(this.state.endTime);
	};

	//  componentDidUpdate(props, state) {
	//   // if (state.city !== this.state.city) {
	//   //   console.log('input changed');
	//   //   this.getData();
	//   // }

	//   // if(state.keyword !== this.state.keyword){
	//   //   this.getData();
	//   // }

	//   // if(state.startTime !== this.state.startTime){
	//   //   this.getData();
	//   // }

	//   // if(state.endTime !== this.state.endTime){
	//   //   this.getData();
	//   // }
	// }

	handleSet = (e) => {
		e.preventDefault();
		if (localStorage.getItem('city') === this.state.city) {
			this.setState({ city: undefined });
		}
		// if(this.state.startTime !== undefined){
		//   this.setState({startTime: localStorage.getItem('startTime')});
		// }
		this.getData();
	};

	handleSearch = (e) => {
		e.preventDefault();

		// if(this.state.startTime !== undefined){
		//   this.setState({startTime: localStorage.getItem('startTime')});
		// }
		this.getData();
	};

	handleVisit = (event) => {
		event.preventDefault();
		const visitdata = {
			event_id: event.target.value,
		};
		console.log(visitdata);
		this.setState({
			event_id: event.target.value,
		});
		Axios.get(`${URL}/event/${event.target.value}`).then((res) => {
			this.setState({
				event_id: event.target.value,
				redirectVar: true,
			});
			console.log(res.status);
		});
	};

	handleRegister = (event) => {
		event.preventDefault();
		const email = localStorage.getItem('email');
		const eventId = parseInt(event.target.value);

		// console.log(visitdata);
		// this.setState({
		//   event_id: event.target.value,
		// });
		// console.log(this.state.event_id);
		// console.log(this.state.redirectVar);
		Axios.post(`${URL}/event/signUpForEvent`, {
			email: email,
			event_id: eventId,
		}).then((res) => {
			console.log(res.status);
		});
	};

