    console.log(startDateTime);
    this.setState({
      startDateTime: startDateTime,
    });
    console.log(this.state.startDateTime);
  }

  streetInputHandler = (event) => {
    console.log(event.target.value);
    const street = event.target.value;
    console.log(street);
    this.setState({
      street: street,
    });
    console.log(this.state.street);
  }

  numberInputHandler = (event) => {
    console.log(event.target.value);
    const number = event.target.value;
    console.log(number);
    this.setState({
      number: number,
    });
    console.log(this.state.number);
  }

  cityInputHandler = (event) => {
    console.log(event.target.value);
    const city = event.target.value;
    console.log(city);
    if(city != ''){
    this.setState({
      city: city,
    });
  }
    console.log(this.state.city);
  }

  stateInputHandler = (event) => {
    console.log(event.target.value);
    const state = event.target.value;
    console.log(state);
    if(state != ''){
    this.setState({
      state: state,
    });
  }
    console.log(this.state.state);
  }

  zipInputHandler = (event) => {
    console.log(event.target.value);
    const zip = event.target.value;
    console.log(zip);
    if(zip != ''){
    this.setState({
      zip: zip,
    });}
    console.log(this.state.zip);
  }

  handleSubmit = (event) => {
    event.preventDefault();
    const {
      name,
      username,
      type,
      gender,
      description,
      street,
      number,
      city,
      state,
      zip
    } = this.state;
    const baseURL = process.env.BACKEND_URL;
    const email = localStorage.getItem('email');
    const password = localStorage.getItem('password');
    const organization = type;
    const fullName = name;
    const screenName = username;
    console.log(email, password, fullName, screenName, organization, gender, description, street, number, city, state, zip);
    console.log(URL);
