import 'react-datepicker/dist/react-datepicker.css';
import '../../styles/Home.css';

import {
	BsFillCalendarWeekFill,
	BsPeopleFill,
	BsPersonFill,
} from 'react-icons/bs';
import { Button, Card, Col, Container, Form, Row } from 'react-bootstrap';

import Axios from 'axios';
import DatePicker from 'react-datepicker';
import { ImLocation } from 'react-icons/im';
import Moment from 'react-moment';
// import { Image as CloudinaryImage } from 'cloudinary-react';
import NavBar from '../../NavBar';
import React from 'react';
import { Redirect } from 'react-router';
import { URL } from '../../env';
import { connect } from 'react-redux';
import moment from 'moment';

class homepage extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			products: [],
			city: localStorage.getItem('city'),
			status: 'active',
			keyword: undefined,
			organizerName: undefined,
			startTime: undefined,
			endTime: undefined,
			event_id: null,
			redirectVar: false,
			search: false,
			resultTable: [],
			searchResults: [],
			time: null,
		};
		this.getSearchResults = this.getSearchResults.bind(this);
		this.handleChange = this.handleChange.bind(this);
		this.handleChangeD = this.handleChangeD.bind(this);
		this.handleChangeK = this.handleChangeK.bind(this);
		this.handleChangeO = this.handleChangeO.bind(this);
		this.handleSearch = this.handleSearch.bind(this);
		this.startTime = this.startTime.bind(this);
		this.endTime = this.endTime.bind(this);
		this.handleSet = this.handleSet.bind(this);
	}

	componentDidMount() {
		this.getData();
	}

	getSearchResults(_keyword) {
		Axios.post(`${URL}/event/keyword`, {
			keyword: _keyword,
		}).then((res) => {
			res.status == 200 && this.setState({ searchResults: res.data });
		});
	}

	getData() {
		const restList = [];
		Axios.get(`${URL}/event/filter`, {
			params: {
				city: this.state.city,
				status: this.state.status,
				keyword: this.state.keyword,
				organizerName: this.state.organizerName,
				startTime: this.state.startTime,
				endTime: this.state.endTime,
			},
		})
			.then((res) => {
				if (res) {
					if (res.data.length >= 0) {
						// eslint-disable-next-line no-plusplus
						for (let i = 0; i < res.data.length; i++) {
							restList.push(res.data[i]);
						}
					}
					this.setState({ products: restList });
				}
			})
			.catch((err) => {
				throw err;
			});

		Axios.get(`${URL}/systemTime`)
      .then((res) => {
        if (res) {
          console.log(res);
          const dt = moment(res.data).format('MM-DD-YYYY hh:mm').toString();
          console.log(dt);
          this.setState({ time: dt });
        }
      }).catch((err) => {
        throw err;
      });
	}

	handleChange = (e) => {
		if (e.target.value === '') {
			this.setState({ city: undefined });
		} else {
			this.setState({ city: e.target.value });
		}
	};

	handleChangeD = (e) => {
		this.setState({ status: e.target.value });
	};

	handleChangeK = (e) => {
		if (e.target.value === '') {
			this.setState({ keyword: undefined });
		} else {
			this.getSearchResults(e.target.value);
			this.setState({ keyword: e.target.value });
		}
	};

	handleChangeO = (e) => {
		if (e.target.value === '') {
			this.setState({ organizerName: undefined });
		} else {
			this.setState({ organizerName: e.target.value });
		}
	};

	startTime = (event) => {
		const startTime = moment(event.target.value)
			.format('YYYY-MM-DDThh:mm:ss')
			.toString();
		this.setState({
			startTime: startTime,
		});
	};

	endTime = (event) => {
		const endTime = moment(event.target.value)
			.format('YYYY-MM-DDThh:mm:ss')
			.toString();
		this.setState({
			endTime: endTime,
		});
	};

	 StatusDisplay = (_eventData) => {
		//  console.log(moment(this.state.time));
		//  console.log(moment(_eventData.signUpDeadline_));
		//  console.log(_eventData.signUpDeadline >= this.state.time);
		//  console.log(moment(_eventData.signUpDeadline) >= this.state.time);
		if(_eventData.isCancelledAndEmailSent){
			return(
				'Cancelled'
			)
		}
		else if(_eventData.signUpDeadline >= this.state.time){
			return(
				'Open For Signup'
			)
		}
		else if(this.state.time > _eventData.signUpDeadline && this.state.time <_eventData.startDateTime){
			return(
				'Signup Closed'
			)
		}
		else if(this.state.time >= _eventData.startDateTime && this.state.time <= _eventData.endDateTime){
			return(
				'Ongoing'
			)
		}
		else{
			return(
				'Finished'
			)
		}
	};

